-- 创建数据表
CREATE TABLE ess_projects (
    id INT AUTO_INCREMENT PRIMARY KEY,
    ess_count INT,
    project_name VARCHAR(255),
    project_application VARCHAR(50),
    area VARCHAR(50),
    country_city VARCHAR(50),
    mw DECIMAL(10, 2),
    mwh DECIMAL(10, 2),
    battery_supplier VARCHAR(255),
    battery_chemistry VARCHAR(50),
    pcs_model VARCHAR(50),
    pcs_numbers INT,
    ess_model1 VARCHAR(50),
    ess_model2 VARCHAR(50),
    ess_numbers1 INT,
    ess_numbers2 INT,
    altitude VARCHAR(50),
    min_temperature VARCHAR(50),
    max_temperature VARCHAR(50),
    contract_time VARCHAR(50),
    crm_or_c4 VARCHAR(50),
    client VARCHAR(255),
    tech_support VARCHAR(255)
);

